from pyvis.network import Network

net = Network(directed=True)

nodes = { str(i[0][0]) for i in src} | { str(i[0][1]) for i in src }
net.add_nodes(nodes)

edges = [(str(i[0][0]),str(i[0][1])) for i in src]
